class Paket_wisatum extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Paket_wisatum_model');
        $this->load->model('Tour_agent_model');
        $this->load->library('pagination');
        $this->load->helper('url');

        if(!$this->session->userdata('level')){
            $this->session->set_flashdata('login_gagal', 'Anda Harus Login terlebih dahulu.');
            redirect('login');
        } else if($this->session->userdata('level') && ($this->session->userdata('level') == "tourguide")){
             $this->session->set_flashdata('login_gagal', 'Halaman Ini hanya dapat diakses oleh admin Tour Agent.');
            redirect('login');
        }
    } 

