    /*
     * Adding a new paket_wisatum
     */




    function cari(){

    $keyword = $this->input->get('cari', TRUE); //mengambil nilai dari form input cari
    $data['paket_wisata'] = $this->Paket_wisatum_model->cari($keyword); //mencari data karyawan berdasarkan inputan
    $data['_view'] = 'layouts/admin/paket_wisatum/cari';
    $this->load->view('layouts/admin/main', $data); //menampilkan data yang sudah dicari
    }
   
    function add()
    {   
        
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params['nama_paketwisata'] = $this->input->post('nama_paketwisata');
            $params['fasilitas'] = $this->input->post('fasilitas');
            $params['harga'] = $this->input->post('harga');
            if (!empty($_FILES['gambar']['name'])) {
                $foto_name    = $this->upload_foto();
                $params['gambar'] = $foto_name;
            }
             $params['id_touragent'] = $this->input->post('id_touragent');
             $params['jadwal'] = $this->input->post('jadwal');
             $params['sk'] = $this->input->post('sk');
            $paket_wisatum_id = $this->Paket_wisatum_model->add_paket_wisatum($params);
            redirect('admin/paket_wisatum');
        }
        else
        {        
            $data['tour_agent']  = $this->Tour_agent_model->get_all_tour_agent(); 
            $data['_view'] = 'layouts/admin/paket_wisatum/add';
            $this->load->view('layouts/admin/main',$data);
        }
    }  
    function upload_foto(){
        $set_name   = "gambar-".RAND(0000,9999);
        $path       = $_FILES['gambar']['name'];
        $extension  = ".".pathinfo($path, PATHINFO_EXTENSION);

        $config['upload_path']          = './resources/upload/paket_wisata/';
        $config['allowed_types']        = 'gif|jpg|jpeg|png';
        $config['max_size']             = 9024;
        $config['file_name']            = "$set_name".$extension;
        $this->load->library('upload', $config);
        // proses upload
        $upload = $this->upload->do_upload('gambar');

        if ($upload == FALSE) {
            $error = array('error' => $this->upload->display_errors());
            dump($error);
            dump('Gambar gagal diupload! Periksa gambar');
        }

        $upload = $this->upload->data();

        return $upload['file_name'];
    }





    /*
     * Editing a paket_wisatum
     */
    function edit($id_paketwisata)
    {   
        // check if the paket_wisatum exists before trying to edit it
        $data['paket_wisatum'] = $this->Paket_wisatum_model->get_paket_wisatum($id_paketwisata);
        
        if(isset($data['paket_wisatum']->id_paketwisata))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params->nama_paketwisata = $this->input->post('nama_paketwisata');
                $params->fasilitas = $this->input->post('fasilitas');
                $params->harga = $this->input->post('harga');
                if (!empty($_FILES['gambar']['name'])) {
                    $foto_name    = $this->upload_foto();
                    $params->gambar = $foto_name;
                }
                $params->id_touragent = $this->input->post('id_touragent');
                $params->jadwal = $this->input->post('jadwal');
                $params->sk = $this->input->post('sk');

                $this->Paket_wisatum_model->update_paket_wisatum($id_paketwisata,$params);            
                redirect('admin/paket_wisatum/index');
            }
            else
            {
                $data['tour_agent']  = $this->Tour_agent_model->get_all_tour_agent(); 
                $data['_view'] = 'layouts/admin/paket_wisatum/edit';
                $this->load->view('layouts/admin/main',$data);
            }
        }
        else
            show_error('The paket_wisatum you are trying to edit does not exist.');
    } 

    /*
     * Deleting paket_wisatum
     */
    function remove($id_paketwisata)
    {
        $paket_wisatum = $this->Paket_wisatum_model->get_paket_wisatum($id_paketwisata);

        // check if the paket_wisatum exists before trying to delete it
        if(isset($paket_wisatum->id_paketwisata))
        {
            $this->Paket_wisatum_model->delete_paket_wisatum($id_paketwisata);
            redirect('admin/paket_wisatum/index');
        }
        else
            show_error('The paket_wisatum you are trying to delete does not exist.');
    }

    function hapusgambar($id)
    {
        $paket_wisatum = $this->Paket_wisatum_model->get_paket_wisatum($id);

        // check if the penginapan produk exists before trying to delete it
        if(isset($paket_wisatum->id_paketwisata))
        {
            unlink('resources/upload/paket_wisata/'.$paket_wisatum->gambar);
            $data['gambar'] = "";
            $this->Paket_wisatum_model->update_paket_wisatum($paket_wisatum->id_paketwisata,$data);
            redirect('admin/paket_wisatum/edit/'.$id);
        }
        else
          show_error('Data Penginapan tidak ada');
    }
    
}
