             echo '<script type="text/javascript">alert("Username sudah digunakan");window.location.replace("'.base_url('Admin/Tour_guide/add').'")</script>';

            // redirect('Admin/Tour_guide/add');
            } 
            else 
            {
            $namaguide = $this->input->post('nama_tourguide');
            // $fotoguide = $this->input->post('gambar');

            $params['nama_tourguide'] = $this->input->post('nama_tourguide');
            $params['tanggal_lahir'] = $this->input->post('tanggal_lahir');
            $params['alamat'] = $this->input->post('alamat');
