    }
    else
        {            
             $data['status'] = '';
            $data['_view'] = 'layouts/admin/tour_guide/add';
            $this->load->view('layouts/admin/main',$data);
        }
    }  

    function upload_foto(){
        $set_name   = "Tour_guide-".RAND(0000,9999);
        $path       = $_FILES['gambar']['name'];
        $extension  = ".".pathinfo($path, PATHINFO_EXTENSION);

        $config['upload_path']          = './resources/upload/user/';
        $config['allowed_types']        = 'gif|jpg|jpeg|png';
        $config['max_size']             = 9024;
        $config['file_name']            = "$set_name".$extension;
        $this->load->library('upload', $config);
        // proses upload
        $upload = $this->upload->do_upload('gambar');

        if ($upload == FALSE) {
            $error = array('error' => $this->upload->display_errors());
            dump($error);
            dump('Gambar gagal diupload! Periksa gambar');
        }

        $upload = $this->upload->data();

        return $upload['file_name'];
    }


    /*
     * Editing a tour_guide
     */
    function edit($id_tourguide)
    {   
        // check if the tour_guide exists before trying to edit it
        $data['tour_guide'] = $this->Tour_guide_model->get_tour_guide($id_tourguide);
        
        if(isset($data['tour_guide']->id_tourguide))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params->nama_tourguide = $this->input->post('nama_tourguide');
                $params->tanggal_lahir = $this->input->post('tanggal_lahir');
                $params->alamat = $this->input->post('alamat');
                $params->no_telp = $this->input->post('no_telp');
                $params->bahasa = $this->input->post('bahasa');
                if (!empty($_FILES['gambar']['name'])) {
                    $foto_name    = $this->upload_foto();
                    $params->gambar = $foto_name;
                }
                $params->harga = $this->input->post('harga');
                $params->status = $this->input->post('status');

                $this->Tour_guide_model->update_tour_guide($id_tourguide,$params);            
                redirect('admin/tour_guide/index');
            }
            else
            {
                $data['status'] = '';
                $data['_view'] = 'layouts/admin/tour_guide/edit';
                $this->load->view('layouts/admin/main',$data);
            }
        }
        else
            show_error('The tour_guide you are trying to edit does not exist.');
    } 

    /*
     * Deleting tour_guide
     */
    function remove($id_tourguide)
    {
        $tour_guide = $this->Tour_guide_model->get_tour_guide($id_tourguide);

        // check if the tour_guide exists before trying to delete it
        if(isset($tour_guide->id_tourguide))
        {
            $this->Tour_guide_model->delete_tour_guide($id_tourguide);
            redirect('admin/tour_guide/index');
        }
        else
            show_error('The tour_guide you are trying to delete does not exist.');
    }
    
}
