        $data['data']=$this->User_model->ambil_data($config['per_page'], $offset);

        $data['user'] = $this->User_model->get_all_user();

        // $data['user'] = $this->User_model->get_all_user();
        
        $data['_view'] = 'layouts/admin/user/index';
        $this->load->view('layouts/admin/main',$data);
    }

