    function upload_foto(){
        $set_name   = "Admin-".RAND(0000,9999);
        $path       = $_FILES['gambar']['name'];
        $extension  = ".".pathinfo($path, PATHINFO_EXTENSION);

        $config['upload_path']          = './resources/upload/user/';
        $config['allowed_types']        = 'gif|jpg|jpeg|png';
        $config['max_size']             = 9024;
        $config['file_name']            = "$set_name".$extension;
        $this->load->library('upload', $config);
        // proses upload
        $upload = $this->upload->do_upload('gambar');

        if ($upload == FALSE) {
            $error = array('error' => $this->upload->display_errors());
            dump($error);
            dump('Gambar gagal diupload! Periksa gambar');
        }

        $upload = $this->upload->data();

        return $upload['file_name'];
    }  

    /*
     * Editing a user
     */
    function edit($id_user)
    {   
        // check if the user exists before trying to edit it
        $data['user'] = $this->User_model->get_user($id_user);
        
        if(isset($data['user']['id_user']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params['nama'] = $this->input->post('nama');
                $params['username'] = $this->input->post('username');
			    $params['password'] = md5($this->input->post('password'));
                $params['level'] = $this->input->post('level');
                if (!empty($_FILES['gambar']['name'])) 
                {
                $foto_name    = $this->upload_foto();
                $params['gambar'] = $foto_name;
                }
					
               

                $this->User_model->update_user($id_user,$params);            
                redirect('admin/user/index');
            }
            else
            {
                $data['_view'] = 'layouts/admin/user/edit';
                $this->load->view('layouts/admin/main',$data);
            }
        }
        else
            show_error('The user you are trying to edit does not exist.');
    } 

    /*
     * Deleting user
     */
    function remove($id_user)
    {
        $user = $this->User_model->get_user($id_user);

        // check if the user exists before trying to delete it
        if(isset($user['id_user']))
        {
            $this->User_model->delete_user($id_user);
            redirect('admin/user/index');
        }
        else
            show_error('The user you are trying to delete does not exist.');
    }

    function hapusgambar($id)
    {
        $user = $this->User_model->get_user($id);

        // check if the penginapan produk exists before trying to delete it
        if(isset($user->id_user))
        {
            unlink('resources/upload/user/'.$user->gambar);
            $data['gambar'] = "";
            $this->User_model->update_user($user->id_user,$data);
            redirect('admin/user/edit/'.$id);
        }
        else
          show_error('Data Penginapan tidak ada');
    }
    
}
